        _ax = OG.Util.round((_endV["x"] - _startV["x"]) / 2);
        _ay = OG.Util.round((_endV["y"] - _startV["y"]) / 2);

        offset["x"] = (offset["x"] == _ax) ? 0 : offset["x"];
        offset["y"] = (offset["y"] == _ay) ? 0 : offset["y"];
